std::ostream &operator<<(std::ostream &os, const cv::Point2d &point) {
    os << "(" << point.x << ", " << point.y << ")";
    return os;
}

// Вспомогательная функция для вывода Polygon2d
std::ostream &operator<<(std::ostream &os, const Utils::Polygon2d &polygon) {
    os << "[";
    for (size_t i = 0; i < polygon.size(); ++i) {
        os << polygon[i];
        if (i < polygon.size() - 1) {
            os << ", ";
        }
    }
    os << "]";
    return os;
}

// Оператор вывода для структуры State
std::ostream &operator<<(std::ostream &os, const State &state) {
    os << "State: " << state.name << "\n";
    os << "Polygons:\n";
    for (size_t i = 0; i < state.polygons.size(); ++i) {
        os << "  Polygon2d " << i + 1 << ": " << state.polygons[i] << "\n";
    }
    return os;
}

